from phonepe.sdk.pg.common.constants import PROD_PG_BASE_URL, UAT_PG_BASE_URL, SIMULATOR_PG_BASE_URL, \
    PROD_EVENT_INGESTION_BASE_URL, UAT_EVENT_INGESTION_BASE_URL
from phonepe.sdk.pg.env import Env


def get_pg_url(env: Env):
    if env == Env.PROD:
        return PROD_PG_BASE_URL
    if env == Env.UAT:
        return UAT_PG_BASE_URL
    if env == Env.SIMULATOR:
        return SIMULATOR_PG_BASE_URL
    return UAT_PG_BASE_URL


def get_event_ingestion_url(env: Env):
    if env == Env.PROD:
        return PROD_EVENT_INGESTION_BASE_URL
    return UAT_EVENT_INGESTION_BASE_URL

