from queue import Queue
from phonepe.sdk.pg.common.constants import MAX_TOTAL_EVENTS_IN_QUEUE


class EventQueueHandler:
    def __init__(self):
        self.queue = None
        self.initialize_queue()

    def initialize_queue(self):
        if self.queue is None:
            self.queue = Queue(maxsize=MAX_TOTAL_EVENTS_IN_QUEUE)

    def put(self, data):
        self.queue.put(data)

    def get(self):
        if self.is_empty():
            return None
        # note: pointer moves to next item for each get
        return self.queue.get()

    def write_data_to_queue(self, ):
        try:
            self.queue.task_done()
        except ValueError as e:
            pass

    def is_empty(self):
        return self.queue is None or self.queue.empty()

    def cur_size(self):
        return self.queue.qsize()

    def clear_queue(self):
        while not self.is_empty():
            self.get()
        self.write_data_to_queue()
        self.initialize_queue()
