from dataclasses import dataclass, field

import phonepe
from phonepe.sdk.pg.common.constants import SDK_TYPE

from dataclasses_json import dataclass_json, LetterCase, config

from phonepe.sdk.pg.payments.models.response_v1.phonepe_response import PhonePeResponse


@dataclass_json(letter_case=LetterCase.CAMEL)
@dataclass
class BaseEvent:
    event_state: str = field(default=None)
    event_type: str = field(default=None)
    flow_type: str = field(default=None)
    host_name: str = field(default=None)
    time: str = field(default=None)
    id: str = field(default=None)
    api_path: str = field(default=None)
    code: str = field(default=None)
    message: str = field(default=None)
    phonepe_response: PhonePeResponse = field(default=None)
    sdk_type: str = field(default=SDK_TYPE)
    sdk_version: str = field(default=phonepe.__version__, metadata=config(field_name="SDKVersion"))
