import requests


class BaseHttpCommand:
    """Makes the requests to the backend"""

    def __init__(self, session=None):
        self.session = session or requests.Session()

    def request(self, method: str, url: str, headers={}, data={}, path_params={}):
        """Makes the request to the backend"""
        # calls appropriate http method get, post
        if method == "GET":
            return self.session.get(url=url, headers=headers, params=path_params)
        if method == "POST":
            return self.session.post(url=url, headers=headers, data=data, params=path_params)


http_command_maker = BaseHttpCommand()
