def merge_dict(dict1, dict2):
    if dict1 is None and dict2 is None:
        return {}
    if dict1 is None:
        return dict2
    if dict2 is None:
        return dict1
    new_dict = dict1.copy()
    old_second_dict = dict2.copy()
    for key in dict2:
        if old_second_dict[key] is None:
            del old_second_dict[key]
    new_dict.update(old_second_dict)
    return new_dict

