from dataclasses_json import dataclass_json, LetterCase
from dataclasses import dataclass, field

from phonepe.sdk.pg.payments.models.request_v1.payment_instrument import PaymentInstrument
from phonepe.sdk.pg.payments.models.request_v1.pg_instrument_type import PgInstrumentType


@dataclass_json(letter_case=LetterCase.CAMEL)
@dataclass
class IntentPaymentInstrument(PaymentInstrument):
    target_app: str = field(default=None)
    type: PgInstrumentType = field(default=PgInstrumentType.UPI_INTENT.value)


