from dataclasses import field

from dataclasses_json import dataclass_json, LetterCase
from dataclasses import dataclass

from phonepe.sdk.pg.payments.models.request_v1.billing_address import BillingAddress
from phonepe.sdk.pg.payments.models.request_v1.expiry import Expiry


@dataclass_json(letter_case=LetterCase.CAMEL)
@dataclass
class NewCardDetails:
    encrypted_card_number: str
    encryption_key_id: int
    card_holder_name: str
    expiry: Expiry
    encrypted_cvv: str
    billing_address: BillingAddress = field(default=None)
