from typing import Optional

from dataclasses_json import dataclass_json, LetterCase
from dataclasses import dataclass, field
from phonepe.sdk.pg.payments.models.response_v1.check_status_models.pg_payment_instrument import PgPaymentInstrument
from phonepe.sdk.pg.payments.models.response_v1.check_status_models.type import Type

# if optional is not mentioned, serialization from json raises warning
# to avoid raising unnecessary warnings, optional is added


@dataclass_json(letter_case=LetterCase.CAMEL)
@dataclass
class CardPaymentInstrumentResponse(PgPaymentInstrument):
    type: Type = field(default=None)
    card_type: Optional[str] = field(default=None)
    pg_transaction_id: Optional[str] = field(default=None)
    bank_transaction_id: Optional[str] = field(default=None)
    pg_authorization_code: Optional[str] = field(default=None)
    arn: Optional[str] = field(default=None)
    bank_id: Optional[str] = field(default=None)
