from dataclasses import field
from typing import Optional

from dataclasses_json import dataclass_json, LetterCase
from dataclasses import dataclass

from phonepe.sdk.pg.payments.models.request_v1.pg_instrument_type import PgInstrumentType
from phonepe.sdk.pg.payments.models.response_v1.pay_response_models.pay_instrument_response_models.instrument_response import \
    InstrumentResponse
from phonepe.sdk.pg.payments.models.response_v1.redirect_info import RedirectInfo


@dataclass_json(letter_case=LetterCase.CAMEL)
@dataclass
class TokenInstrumentResponse(InstrumentResponse):
    type: PgInstrumentType = field(default=None)
    redirect_info: Optional[RedirectInfo] = field(default=None)
